(()=>{document.addEventListener("DOMContentLoaded",()=>{if(document.querySelector(".right-sidebar")){let e=Element.prototype.scrollTo,o=Element.prototype.scrollIntoView;Element.prototype.scrollTo=function(...t){if(!this.closest(".right-sidebar"))return e.apply(this,t)},Element.prototype.scrollIntoView=function(...t){if(!this.closest(".right-sidebar"))return o.apply(this,t)}}document.querySelectorAll('.right-sidebar a[href^="#"]').forEach(e=>{e.addEventListener("click",o=>{o.preventDefault();let t=e.getAttribute("href");if(t&&t!=="#"){let r=document.querySelector(t);if(r){let n=r.getBoundingClientRect().top+window.pageYOffset;window.scrollTo({top:n,behavior:"auto"}),history.pushState(null,null,t)}}})})});})();
